import React, { useEffect, useState } from 'react'
import { Ships } from '../../components/ships/component'
import { LoadMsg } from '../../components/Loading/component'
import { ErrMsg } from '../../components/Error/component'
import { getHhips } from '../../services/spacex'

export const ShipsSite = () => {
  const [ships, setships] = useState<any>([])
  const [Load, setIsLoading] = useState(false)
  const [Err, setIsError] = useState(false)

  useEffect(() => {
    async function get() {
      try {
        setIsLoading(true)
        const shipsData = await getships()
        setships(shipsData)
        setIsLoading(false)
      } catch (e) {
        console.error(e)
        setIsError(true)
      }
    }
    get()
  }, [])

  console.log(ships)

  const mappedships = ships.map((ship: any) => (
    <ships key={ships.id} ship={ships} />
  ))

  if (Err && !Load) {
    return (
      <div>
        <ErrMsg />
      </div>
    )
  }

  return !Err && Load ? (
    <LoadMsg />
  ) : (
    <div>{mappedships}</div>
  )
}
